import VueRouter from "vue-router";
import Vue from "vue";
import Entertain from "@/components/Entertain.vue";
import Learn from "@/components/Learn.vue";
import Tool from "@/components/Tool.vue";
import Add from "@/components/Add.vue";
import Delect from "@/components/Delect.vue";
import Login from "@/components/Login.vue";
import Container from "@/components/Container.vue";
import MainMenu from "@/components/MainMenu.vue";
import store from "@/store";

Vue.use(VueRouter)

const router = new VueRouter({
    routes: [
      // 动态路径参数 以冒号开头
      { path:'/', redirect:'/main'},

      
        
      { name:'login', path:'/login', component:MainMenu, meta: { requireAuth: false }},
          
      { name: 'main', path: '/main', component:MainMenu, meta: { requireAuth: false } ,children:[
        { name: 'learn', path: 'learn', component: Learn, meta: { requireAuth: false }},
        { name: 'entertain', path: 'entertain', component: Entertain, meta: { requireAuth: false }},
        { name: 'tool', path: 'tool', component: Tool,  meta: { requireAuth: false }},
        { name: 'add', path: 'add', component: Add, meta: { requireAuth: true }},
        { name: "delect", path:'delect', component: Delect, meta: { requireAuth: true }}
      ]}

      
      
      ]

    })

    router.beforeEach((to, from, next) => {
      const token = localStorage.getItem('token')
      console.log("token", token)
      console.log(store.state.showLoginModal)
      if(to.meta.requireAuth && !token){
        console.log("没有token，跳转到登录页面")
        // 如果没有token，跳转到登录页面
        this.$store.commit('showLoginModal');
        
        next('/login');
      }else
      {
        next();
      }
    })
      
    

  export default router
