import VueRouter from "vue-router";
import Vue from "vue";
import Entertain from "@/components/Entertain.vue";
import Learn from "@/components/Learn.vue";
import Tool from "@/components/Tool.vue";
import Add from "@/components/Add.vue";
import Delect from "@/components/Delect.vue";
import Login from "@/components/Login.vue";
import Container from "@/components/Container.vue";
import MainMenu from "@/components/MainMenu.vue";

Vue.use(VueRouter)

const router = new VueRouter({
    routes: [
      // 动态路径参数 以冒号开头
      { path:'/', redirect:'/main'},

      
        
      { name:'login', path:'/login', component:Login},
          
      { name: 'main', path: '/main', component:MainMenu, children:[
        { name: 'learn', path: 'learn', component: Learn },
        { name: 'entertain', path: 'entertain', component: Entertain},
        { name: 'tool', path: 'tool', component: Tool},
        { name: 'add', path: 'add', component: Add, meta: { requireAuth: true}},
        { name: "delect", path:'delect', component: Delect, meta: { requireAuth: true}}
      ]}

      
      
      ]

    })

    router.beforeEach(to, from, next)

  export default router
