  methods: {
    onSubmit() {
      switch (this.form.first) {
        //学习类目的请求
        case "learn":
          axios.post('http://localhost:8088/insertSite_Learn', {
            name: this.form.name,
            url: this.form.url,
            type: this.form.second
          }).then(response => {
            this.$message(response.data);
          });
          break;
        case "entertain":
          axios.post('http://localhost:8088/insertSite_Entertain', {
            name: this.form.name,
            url: this.form.url,
            type: this.form.second
          }).then(response => {
            this.$message(response.data);
          });
          break;
        case "tool":
          axios.post('http://localhost:8088/insertSite_Tool', {
            name: this.form.name,
            url: this.form.url,
            type: this.form.second
          }).then(response => {
            this.$message(response.data);
          });
          break;
      }
    },
    firstChange(first) {
      switch (first) {
        case "learn":
          axios.post('http://localhost:8088/getSite_Type', {
            type: "学习"
          }).then(response => {
            this.form.item1 = response.data;
          });
          break;
        case "entertain":
          axios.post('http://localhost:8088/getSite_Type', {
            type: "娱乐"
          }).then(response => {
            this.form.item1 = response.data;
          });
          break;
        case "tool":
          axios.post('http://localhost:8088/getSite_Type', {
